import React,{Component} from "react";
import * as d3 from 'd3'

class Tree_chart extends Component{
  constructor(props) {
    super(props);
    this.data = props.chartState
  }

  drawTree (data) {
    const padding = this.data.padding
    const width = this.data.width
    const pieHeight = this.data.pieHeight

    const svg = d3.select("body")
      .append("svg")
      .attr("width", this.data.width)
      .attr("height", this.data.pieHeight)
      .style('padding-left', padding.left)
      .style('padding-top', padding.top)

    // 初始化树状图数据获取器
    let tree = d3.tree()
      .size([width, pieHeight - 80])
      .separation(function (a, b) {
        return (a.parent === b.parent ? 1 : 2) / a.depth
      })

    // 初始化json数据转成一棵树，这个步骤是非常必要的！！
    let root = d3.hierarchy(data)
      .sum(function (d) {
        return d.value
      })
    console.log(root)
    // 初始化树状图
    let treeData = tree(root).descendants

    // 画圆
    svg.selectAll('circle')
      .data(nodes)
      .enter()
      .append('circle')
      .style('fill', function (d) { return color(d.depth) })
      // .style('fill', function (d) { return color[d.depth] })
      .attr('cx', function (d) { return d.x })
      .attr('cy', function (d) { return d.y })
      .attr('r', function (d) { return d.r })
      .on('mouseover', function (d) {
        d3.event.target.style.stroke = 'yellow'
        // d3.select(this).style('stroke', 'white')
        d3.event.target.style.strokeWidth = '2px'
        svg.append('text')
          .style('fill', 'black')
          .style('opacity', '0.5')
          .attr('class', 'hello')
          .attr('x', d3.event.offsetX + 20)
          .attr('y', d3.event.offsetY - 10)
          .text(d.data.name)
      })
      .on('mouseout', function (d) {
        svg.selectAll('.hello').remove()
        d3.event.target.style.strokeWidth = '0px'
      })

    // 文字
    svg.selectAll('text')
      .data(nodes)
      .enter()
      .append('text')
      .style('fill', 'black')
      .style('fill-opacity', function (d) { return d.children ? 0 : 1 }) // 设置包含子节点的文字不显示
      .attr('x', function (d) { return d.x })
      .attr('y', function (d) { return d.y })
      .attr('dy', '.3em')
      .attr('dx', function (d) { return '-' + d.data.name.length / 2 + 'em' }) // 设置文字居中，通过dx 负文字个数的二分之一
      .text(function (d) { return d.data.name })
      .style('font-size', function (d) { return 12 - d.depth + 'px' })


  }

  componentDidMount() {
    this.drawTree(this.data.packData)
  }

  render() {
    return <div></div>
  }

}

export default Tree_chart
