import React from 'react';
import * as d3 from 'd3'
export default class BarChart extends React.Component{
  constructor() {
    super()
    this.state = {
      dataset: [ 5, 10, 15, 20, 25, 30, 35],
      count: 0
    }
  }
  drawBar = () => {
    let svg = d3.select("body")
      .append("svg")
      .attr("width", '200px')
      .attr("height", '200px')

    let bars = svg.selectAll('rect')
      .data(this.state.dataset)
      .enter()
      .append('rect')

    bars.attr("x", (d, i) => i * 40)
      .attr('y', d => 500 - d * 4)
      .attr('width', 30)
      .attr("height", d => d * 4)
      .attr('fill','green')
      .transition()
      .duration(1000)
      .delay(function(d,i){
        return 50*i;
      })
      .attr('fill', 'blue')

    //加标签
    svg.selectAll('text')
      .data(this.state.dataset)
      .enter()
      .append('text')
      .text(d => d)
      .attr("x", (d,i) => i * 40 + 10)
      .attr("y",d => 500-(d*4) - 6)
      .attr('font-family','sans-serif')
      .attr('font-size','11px')
      .attr('fill','blue')
  }

  componentDidMount() {
    this.drawBar()
  }


  render() {
    return (
      <div>
      </div>
    )
  }
}
