import {action, observable} from "mobx";

class chartState {
  width = 700
  height = 500
  pieHeight = 700
  padding = { top:20, right:20, bottom:100, left:100 }

  @observable dataSet = [50,43,120,87,99,167,142]

  pieData = [30 , 10 , 43 , 55 , 13, 65]

  forceNodes = [
    { name: "GuiLin"    },
    { name: "GuangZhou" },
    { name: "XiaMen"    },
    { name: "HangZhou"   },
    { name: "ShangHai"   },
    { name: "QingDao"    },
    { name: "TianJin"    },
    { name: "BeiJing"    },
    { name: "ChangChun"  },
    { name: "XiAn"       },
    { name: "WuluMuQi"  },
    { name: "LaSa"       },
    { name: "ChengDu"    } ];

  forceEdges = [
      { source : 0  , target: 1 } ,
      { source : 1  , target: 2 } ,
      { source : 2  , target: 3 } ,
      { source : 3  , target: 4 } ,
      { source : 4  , target: 5 } ,
      { source : 5  , target: 6 } ,
      { source : 6  , target: 7 } ,
      { source : 7  , target: 8 } ,
      { source : 8  , target: 9 } ,
      { source : 9  , target: 10 } ,
      { source : 10 , target: 11 } ,
      { source : 11 , target: 12 } ,
      { source : 12 , target: 0 }
    ];

  @action.bound changeData = () => {
    this.dataSet.shift()
    this.dataSet.push(Math.floor(Math.random() * 100))
  }

}

export default new chartState()
