import React,{Component} from "react";
import * as d3 from 'd3'

class Force_chart extends Component{
  constructor(props) {
    super(props)
    this.data = props.chartState
  }

  drawForceChart (nodes, edges) {
    const padding = this.data.padding
    const width = this.data.width
    const pieHeight = this.data.pieHeight

    const svg = d3.select("body")
      .append("svg")
      .attr("width", width)
      .attr("height", pieHeight)
      .style('padding-left', padding.left)
      .style('padding-top', padding.top)

    const force = d3.forceSimulation(nodes)
                    .force("charge", d3.forceManyBody())
                    .force("link", d3.forceLink(edges))
                    .force("center", d3.forceCenter());

    const svg_edges = svg.selectAll("line")
                          .data(edges)
                          .enter()
                          .append("line")
                          .style("stroke","#ccc")
                          .style("stroke-width",1)
                          .on("mouseover",function(d,i){
                            d3.select(this)
                              .style("stroke","grey")
                              .style("stroke-width",3)
                          })
                          .on("mouseout",function(d,i){
                            d3.select(this)
                              .style("stroke","#ccc")
                              .style("stroke-width",1)
                          })
    const color = d3.schemeSet2
    const svg_nodes = svg.selectAll("circle")
                          .data(nodes)
                          .enter()
                          .append("circle")
                          .attr("r",10)
                          .style("fill",function(d,i){
                            return color[i];
                          })
                          .call(force.drag)
                          // .on('mouseover', d => showDetail(d))
                          // .on("mouseover",function(d,i){
                          //   d3.select(this)
                          //     .attr("r",'20px')
                          //   d3.select('#tooltip')
                          //     .classed('hidden', false)
                          //     .style('left', (d.x - 50))
                          //     .style('top', (d.y-40))
                          //     .select('#place').text(d.name)
                          // })
                          // // .on('mouseout', d => hideDetail())
                          // .on('mouseout', d => {
                          //   d3.select(this)
                          //     .attr("r",'10px')  //鼠标移出时，设置圆点半径报错
                          //   d3.select('#tooltip').classed('hidden', true)
                          //     .select('#place').text()
                          // })

    force.on("tick", function(){
      svg_edges.attr("x1",function(d){ return d.source.x; });
      svg_edges.attr("y1",function(d){ return d.source.y; });
      svg_edges.attr("x2",function(d){ return d.target.x; });
      svg_edges.attr("y2",function(d){ return d.target.y; });

      svg_nodes.attr("cx",function(d){ return d.x; });
      svg_nodes.attr("cy",function(d){ return d.y; });
    });
  }

  componentDidMount() {
    this.drawForceChart(this.data.forceNodes, this.data.forceEdges)
  }

  render() {
    return (
      <div></div>
    )
  }

}

export default Force_chart
