import React, {Component} from 'react';
import * as d3 from "d3";
import {observer} from "mobx-react";

@observer
class BarChart_2 extends Component {
  constructor(props) {
    super(props)
    this.chartState = this.props.chartState
  }


  drawChart(data) {
    const padding = this.chartState.padding

    const xScale = d3.scaleLinear()
      .domain([0, d3.max(data)])
      .range([0, 500])

    const svg = d3.select("body").append("svg").attr("width", this.chartState.width).attr("height", this.chartState.height).style('padding-left', 50)
    const bars = svg.selectAll('rect').data(data).enter().append('rect')
    bars.attr("x",function(d,i){    //设置X坐标
      return padding.left + xScale(i);
    }).attr('y', (d, i) => this.chartState.height - padding.bottom - d * 10)
      .attr('width', 30).attr('height', (d, i) => d * 10).attr('fill', 'green')

    const text = svg.selectAll('text').data(data).enter().append('text')
    text.text((d, i) => d).attr('fill', 'blue').attr("font-size", 12).attr("text-anchor","middle")
                          .attr('x', (d, i) => padding.left + xScale(i) + 15).attr('y', (d, i) => this.chartState.height - padding.bottom - d * 10 - 5)


    const xAxis = d3.axisBottom(xScale)
    svg.append("g")
      .attr("transform","translate(" + padding.left + "," + (this.chartState.height - padding.bottom) + ")")
      .call(xAxis)




  }

  timer = () => {
    setInterval(() => {
      d3.select("svg").remove()
      this.drawChart(this.props.chartState.dataSet)
      this.props.chartState.changeData()
    }, 3000)
  }

  componentDidMount() {
    this.timer()
  }

  render() {
    return <div></div>
  }
}

export default BarChart_2
