import React, {Component} from 'react';
import * as d3 from "d3";

class BarChart_2 extends Component {
  constructor(props) {
    super(props)
    this.state = {
      width: 700,
      height: 300,

    }
  }

  drawChart() {
    const data = [12, 5, 6, 6, 9, 10]
    const svg = d3.select("body").append("svg").attr("width", this.state.width).attr("height", this.state.height);
    const bars = svg.selectAll('rect').data(data).enter().append('rect')
    bars.attr('x', (d, i) => i * 40).attr('y', 0).attr('width', 25).attr('height', (d, i) => 300 - d * 10).attr('fill', 'green')
  }


  componentDidMount() {
    this.drawChart();
  }

  render() {
    return <div></div>
  }
}

export default BarChart_2;
