import React, {Component} from 'react';
import * as d3 from "d3";
import {observer} from "mobx-react";

// @observer
export  default class BarChart_2 extends Component {
  constructor(props) {
    super(props)
    console.log(this.props.chartState.dataSet)
  }

  drawChart() {
    const data = [12, 5, 6, 6, 9, 10]
    const svg = d3.select("body").append("svg").attr("width", 700).attr("height", 300);
    const bars = svg.selectAll('rect').data(data).enter().append('rect')
    bars.attr('x', (d, i) => i * 40).attr('y', (d, i) => 300 - d * 10).attr('width', 25).attr('height', (d, i) => d * 10).attr('fill', 'green')
    const text = svg.selectAll('text').data(data).enter().append('text')
    text.text((d, i) => d).attr('fill', 'blue').attr('x', (d, i) => i * 40 + 10).attr('y', (d, i) => 300 - d * 10 - 10)
    // 生成1-100以内的整数
    let nextData = () => {
      return Math.floor(Math.random()*10)
    }

    // 修改柱状图绑定的数组，并移除画布，进行重绘
    let pushData = () => {
      data.shift()
      data.push(nextData())
      console.log(data)
      // 不要用"body"，否则不会移除画布
      // d3.select("svg").remove()
      // d3.select("body").append("svg").attr("width", this.state.width).attr("height", this.state.height)
      // svg.selectAll('rect').data(data).enter().append('rect')
      //   .attr('x', (d, i) => i * 40).attr('y', (d, i) => this.state.height - d * 10).attr('width', 25).attr('height', (d, i) => d * 10).attr('fill', 'green')
      // svg.selectAll('text').data(data).enter().append('text')
      //   .text((d, i) => d).attr('fill', 'blue').attr('x', (d, i) => i * 40 + 10).attr('y', (d, i) => this.state.height - d * 10 - 10)
    }

    pushData()
  }

  timer = () => {
    setInterval(() => {
      d3.select("svg").remove()
      this.drawChart()
    }, 1000)
  }

  componentDidMount() {
    this.timer()

  }

  render() {
    return <div></div>
  }
}
