import React,{Component} from "react";
import * as d3 from 'd3'

class Force_chart extends Component{
  constructor(props) {
    super(props)
    this.data = props.chartState
  }

  drawForceChart (nodes, edges) {
    const padding = this.data.padding
    const width = this.data.width
    const pieHeight = this.data.pieHeight

    const svg = d3.select("body")
      .append("svg")
      .attr("width", width)
      .attr("height", pieHeight)
      .style('padding-left', padding.left)
      .style('padding-top', padding.top)

    const g = svg.append('g'); // 则svg中创建g

    const simulation = d3.forceSimulation(nodes) // 指定被引用的nodes数组
                          .force('link', d3.forceLink(edges).distance(150))
                          .force('collision', d3.forceCollide(1).strength(0.1))
                          .force('center', d3.forceCenter(width / 2, pieHeight / 2))
                          .force('charge', d3.forceManyBody().strength(-1000).distanceMax(800));

    const edgesLine = svg.selectAll('line')
                          .data(edges) // 绑定数据
                          .enter() // 为数据添加对应数量的占位符
                          .append('path') // 在占位符上面生成折线（用path画）
                          .attr('d', (d) => { return d && 'M ' + d.source.x + ' ' + d.source.y + ' L ' + d.target.x + ' ' + d.target.y; }) //遍历所有数据。d表示当前遍历到的数据，返回绘制的贝塞尔曲线
                          .attr('id', (d, i) => { return i && 'edgepath' + i; }) // 设置id，用于连线文字
                          .attr('marker-end', 'url(#arrow)') // 根据箭头标记的id号标记箭头
                          .style('stroke', '#000') // 颜色
                          .style('stroke-width', 1); // 粗细

    const nodesCircle = svg.selectAll('circle')
                            .data(nodes)
                            .enter()
                            .append('circle') // 创建圆
                            .attr('r', 30) // 半径
                            .style('fill', '#9FF') // 填充颜色
                            .style('stroke', '#0CF') // 边框颜色
                            .style('stroke-width', 2) // 边框粗细
                            .on('click', (node) => { // 点击事件
                              console.log('click');
                            })
                            .call(drag); // 拖拽单个节点带动整个图

    nodesCircle.append('title')
      .text((node) => { // .text设置气泡提示内容
        return node.name; // 气泡提示为node的名称
      });

    function onDragStart(d) {
      // console.log('start');
      // console.log(d3.event.active);
      if (!d3.event.active) {
        simulation.alphaTarget(1) // 设置衰减系数，对节点位置移动过程的模拟，数值越高移动越快，数值范围[0，1]
          .restart();  // 拖拽节点后，重新启动模拟
      }
      d.fx = d.x;    // d.x是当前位置，d.fx是静止时位置
      d.fy = d.y;
    }
    function dragging(d) {
      d.fx = d3.event.x;
      d.fy = d3.event.y;
    }
    function onDragEnd(d) {
      if (!d3.event.active) simulation.alphaTarget(0);
      d.fx = null;       // 解除dragged中固定的坐标
      d.fy = null;
    }
    const drag = d3.drag()
      .on('start', onDragStart)
      .on('drag', dragging) // 拖拽过程
      .on('end', onDragEnd);


    const svg_edges = svg.selectAll("line")
                          .data(edges)
                          .enter()
                          .append("line")
                          .style("stroke","#ccc")
                          .style("stroke-width",1)
                          .on("mouseover",function(d,i){
                            d3.select(this)
                              .style("stroke","grey")
                              .style("stroke-width",3)
                          })
                          .on("mouseout",function(d,i){
                            d3.select(this)
                              .style("stroke","#ccc")
                              .style("stroke-width",1)
                          })
    const color = d3.schemeSet2
    const svg_nodes = svg.selectAll("circle")
                          .data(nodes)
                          .enter()
                          .append("circle")
                          .attr("r",10)
                          .style("fill",function(d,i){
                            return color[i];
                          })
                          .call(force.drag)
                          // .on('mouseover', d => showDetail(d))
                          // .on("mouseover",function(d,i){
                          //   d3.select(this)
                          //     .attr("r",'20px')
                          //   d3.select('#tooltip')
                          //     .classed('hidden', false)
                          //     .style('left', (d.x - 50))
                          //     .style('top', (d.y-40))
                          //     .select('#place').text(d.name)
                          // })
                          // // .on('mouseout', d => hideDetail())
                          // .on('mouseout', d => {
                          //   d3.select(this)
                          //     .attr("r",'10px')  //鼠标移出时，设置圆点半径报错
                          //   d3.select('#tooltip').classed('hidden', true)
                          //     .select('#place').text()
                          // })

    force.on("tick", function(){
      svg_edges.attr("x1",function(d){ return d.source.x; });
      svg_edges.attr("y1",function(d){ return d.source.y; });
      svg_edges.attr("x2",function(d){ return d.target.x; });
      svg_edges.attr("y2",function(d){ return d.target.y; });

      svg_nodes.attr("cx",function(d){ return d.x; });
      svg_nodes.attr("cy",function(d){ return d.y; });
    });
  }

  componentDidMount() {
    this.drawForceChart(this.data.forceNodes, this.data.forceEdges)
  }

  render() {
    return (
      <div></div>
    )
  }

}

export default Force_chart
