import React,{Component} from "react";
import * as d3 from 'd3'

class Pie_chart extends Component{
  constructor(props) {
    super(props);
    this.data = props.chartState
  }

  drawPie (data) {
    const padding = this.data.padding

    const svg = d3.select("body")
      .append("svg")
      .attr("width", this.data.width)
      .attr("height", this.data.pieHeight)
      .style('padding-left', padding.left)
      .style('padding-bottom', padding.bottom)

    // 将数据转化成圆环图需要的数据结构
    const pie = d3.pie()(data)

    // 设置圆环内外半径大小
    const outerRadius = this.data.width / 2;
    const innerRadius = this.data.pieHeight / 4;

    // 以半径大小创建圆环的圆弧
    const arc = d3.arc()
                  .innerRadius(innerRadius)
                  .outerRadius(outerRadius)
    // 根据前面的圆环图数据创建
    const arcs = svg.selectAll("g")
                    .data(pie)
                    .enter()
                    .append("g")
                    .attr("transform","translate("+outerRadius+","+outerRadius+")");
    // 这一步是画图的关键
    arcs.append("path")
      .attr("fill", 'red')
      .attr("d",function(d){
        return arc(d);
      });

    arcs.append("text")
      .attr("transform",function(d){
        return "translate(" + arc.centroid(d) + ")"
      })
      .attr("text-anchor","middle")
      .text(function(d){
        return d.value
      })
  }

  componentDidMount() {
    this.drawPie(this.data.pieData)
  }

  render() {
    return <div></div>
  }

}

export default Pie_chart
