import React,{Component} from "react";
import * as d3 from 'd3'

class ChordChart extends Component{
  constructor(props) {
    super(props);
    this.data = props.chartState
  }

  drawChord (data) {
    const padding = this.data.padding
    const width = this.data.width
    const pieHeight = this.data.pieHeight
    const radius = this.data.radius

    const svg = d3.select("body")
      .append("svg")
      .attr("width", this.data.width)
      .attr("height", this.data.pieHeight)
      .style('padding-left', padding.left)
      .style('padding-top', padding.top)

    const chord = d3.chord()(data)
    console.log(chord)

    // 设置圆环内外半径大小
    const outerRadius = this.data.width / 4;
    const innerRadius = this.data.pieHeight / 6;

    // 设置颜色函数
    const color = d3.scaleOrdinal(d3.schemeCategory10)

    // 以半径大小创建圆环的圆弧
    const arc = d3.arc()
      .innerRadius(innerRadius)
      .outerRadius(outerRadius)
    // .cornerRadius(20)   // 环的圆角

    // 根据前面的圆环图数据来创建一个组合，这个组合用来包含之后的圆弧线
    const g = svg.selectAll("g")
      .data(chord)  // 只能在这里绑定数据才有用，先绑定数据再去画线
      .enter()
      .append("g")
      .attr("transform","translate(" + (outerRadius*1.2) + "," + (outerRadius*1.2) + ")");

    // 这一步是画图的关键，画线
    g.append("path")
      .attr("fill", (d, i) => {
        return color[d.index]
      })
      // 这一步必不可少
      .attr("d",function(d){
        return arc(d)
      })



  }

  componentDidMount() {
    this.drawChord(this.data.matrix)
  }

  render() {
    return <div></div>
  }

}

export default ChordChart
