import React,{Component} from "react";
import * as d3 from 'd3'

class Force_chart extends Component{
  constructor(props) {
    super(props)
    this.data = props.chartState
  }

  drawForceChart (nodes, edges) {
    const padding = this.data.padding

    const svg = d3.select("body")
      .append("svg")
      .attr("width", this.data.width)
      .attr("height", this.data.pieHeight)
      .style('padding-left', padding.left)
      .style('padding-top', padding.top)

    const force = d3.force()
                    .nodes(nodes)
                    .links(edges)
                    .size([width,height])
                    .linkDistance(100)
                    .charge([-200])
                    .start()
    console.log(force)


  }

  componentDidMount() {
    this.drawForceChart(this.data.forceNodes, this.data.forceEdges)
  }

  render() {
    return (
      <div></div>
    )
  }

}

export default Force_chart
